def longest_increasing_subsequence(nums):
    if not nums:
        return []

    n = len(nums)
    dp = [[nums[i]] for i in range(n)]

    for i in range(1, n):
        for j in range(i):
            if nums[i] > nums[j] and len(dp[j]) + 1 > len(dp[i]):
                dp[i] = dp[j] + [nums[i]]

    return max(dp, key=len)
